                __syncthreads();

                // Main computation loop with double buffering
                #pragma unroll 8
                for (int k_tile = 0; k_tile < K/BK; ++k_tile) {
                    int next_buf = 1 - current_buf;

                    // Prefetch next tile while computing current tile
                    if (k_tile + 1 < K/BK) {
