                        // Load next B tile with vectorized memory access
                        #pragma unroll
                        for (int i = 0; i < 2; i++) {
                            int row = (k_tile + 1) * BK + ty * 2 + i;

                            // Use float4 vectorized loads when aligned properly
                            if ((tx * 8) % 4 == 0) {
