                    if (ty + 24 < 32 && tile * 32 + ty + 24 < K) {
                        b_offset = col_start * K + tile * 32 + ty + 24;
                        Bs[ty + 24][tx] = B[b_offset];
                    }
                    else if (ty + 24 < 32) {
                        Bs[ty + 24][tx] = 0.0f;
                    }

                    if (ty + 28 < 32 && tile * 32 + ty + 28 < K) {
                        b_offset = col_start * K + tile * 32 + ty + 28;
                        Bs[ty + 28][tx] = B[b_offset];
                    }
                    else if (ty + 28 < 32) {
