        AT_DISPATCH_FLOATING_TYPES_AND_HALF(input.scalar_type(), "softplus_cuda_forward", ([&] {
            softplus_cuda_kernel<scalar_t><<<blocks, threads>>>(
                input.data_ptr<scalar_t>(),
                output.data_ptr<scalar_t>(),
                size);
        }));
    }

    return output;
}

