    #pragma unroll
    for (int stride = blockDim.x / 2; stride > 32; stride >>= 1) {
        if (tid < stride) {
            shared_mem[tid] += shared_mem[tid + stride];
        }
        __syncthreads();
    }

    // Final reduction within a warp using warp shuffle
    if (tid < 32) {
        // Warp-level reduction using shuffle
        scalar_t val = shared_mem[tid];

