                if (tid < WARP_SIZE) {
                    dot_sum = (tid < num_warps) ? s_dot[tid] : 0.0f;
                    pred_norm_sq = (tid < num_warps) ? s_pred_norm_sq[tid] : 0.0f;
                    target_norm_sq = (tid < num_warps) ? s_target_norm_sq[tid] : 0.0f;

