                    if (x < width) {
                        const int idx = base_idx + x;

                        // Load value
                        half val = output[idx];

                        // Add bias
                        val = __hadd(val, bias_val);

                        // First clamp
                        val = __hmin(__hmax(val, zero), one);

                        // Scale
                        val = __hmul(val, scaling_factor);

                        // Second clamp
                        val = __hmin(__hmax(val, zero), one);

                        // Divide
                        val = __hdiv(val, scaling_factor);

                        // Store result
                        output[idx] = val;
                    }
                }
            }
        }
