        // Process remaining batches
        for (; n < batch_size; n++) {
            for (int c = 0; c < channels; c++) {
                const int idx = n * stride_batch + c * stride_channel + pos;

                scalar_t val = output[idx];
                val = mish(val);
                val += add_value;
                val = max(scalar_t(-1.0), min(scalar_t(1.0), val));
                val *= scale;
                output[idx] = val;
            }
        }
    }
