                          (threads_per_block * elements_per_thread);

    // Launch optimized kernel
    vectorized_conv2d_kernel<<<num_blocks, threads_per_block>>>(
        input.data_ptr<float>(),
        output.data_ptr<float>(),
        batch_size,
        in_channels,
        out_channels,
